import React, { useState, useEffect, useRef } from 'react';
import './BookInvite.css';
import { Howl } from 'howler';

import musicFile from './assets/nikah-music.mp3';
import coverImage from './assets/cover.jpg';
import leftImage from './assets/left.jpg';
import rightImage from './assets/right.jpg';

export default function BookInvite() {
  const [open, setOpen] = useState(false);
  const [shake, setShake] = useState(false);
  const audioRef = useRef(null);

  useEffect(() => {
    audioRef.current = new Howl({ src: [musicFile], html5: true });
  }, []);

  useEffect(() => {
    if (open) audioRef.current.play();
  }, [open]);

  const handleClick = () => {
    setShake(true);
    setTimeout(() => {
      setShake(false);
      setOpen(true);
    }, 500);
  };

  return (
    <div className={`book-container ${open ? 'open' : ''}`}>
      {!open && (
        <div className={`book-cover ${shake ? 'shake' : ''}`} onClick={handleClick}>
          <div className="circle-image">
            <img src={coverImage} alt="Cover" />
          </div>
          <h2 className="cover-title">You're Invited</h2>
          <p className="cover-sub">To our Nikah Ceremony</p>
        </div>
      )}

      {open && (
        <div className="book-pages">
          <div className="page left">
            <div className="circle-image">
              <img src={leftImage} alt="Left" />
            </div>
            <p className="page-text">Nazeem ❤️</p>
          </div>

          <div className="page right">
            <div className="circle-image">
              <img src={rightImage} alt="Right" />
            </div>
            <p className="page-text">❤️ Faiza</p>
          </div>
          <div className="flowers" />
        </div>
      )}
    </div>
  );
}
