import React, { useRef, useEffect, useState } from 'react';
import { Howl } from 'howler';
import './styles.css';

import musicFile from '../src/assets/Nikah-Music.mp3';
import leftImage from '../src/assets/nikah-inside-left.jpg'; // Replace with your image

export default function NikahCard() {
  const [flipped, setFlipped] = useState(false);
  const audioRef = useRef(null);

  useEffect(() => {
    const sound = new Howl({
      src: [musicFile],
      html5: true,
    });
    audioRef.current = sound;
    return () => sound.unload();
  }, []);

  const handleCardClick = () => {
    setFlipped(!flipped);
    if (!flipped) audioRef.current.play();
    else audioRef.current.stop();
  };

  return (
    <div className={`card-container ${flipped ? 'shower' : ''}`} onClick={handleCardClick}>
      <div className={`card ${flipped ? 'flipped' : ''}`}>
        <div className="card-face front">
          <h2 className="invite-title">You're Invited!</h2>
          <h3 className="invite-names">Nazeem ‚ù§Ô∏è Faiza</h3>
          <p className="invite-date">12th November 2024</p>
        </div>

        <div className="card-face back">
          <div className="leftImage" style={{ backgroundImage: `url(${leftImage})` }} />
          <div className="rightContent">
            <h2>Nikah Ceremony</h2>
            <p>We joyfully invite you to celebrate the wedding of</p>
            <h3>Nazeem & Faiza</h3>
            <p>üìç Royal Pearl Convention, Hyderabad</p>
            <p>üïí 6:00 PM Onwards</p>
          </div>
        </div>
      </div>
      <div className="flowers" />
    </div>
  );
}
