import React, { useRef, useEffect } from 'react';
import { Howl } from 'howler';
import musicFile from '../src/assets/Nikah-Music.mp3';

export default function NikahCard() {
  const audioRef = useRef(null);

  useEffect(() => {
    const sound = new Howl({ src: [musicFile], html5: true });
    audioRef.current = sound;
    return () => sound.unload();
  }, []);

  const handleMouseOver = () => {
    audioRef.current.play();
  };

  const handleMouseOut = () => {
    audioRef.current.pause();
    audioRef.current.seek(0);
  };

  return (
    <div className="invitationCard" onMouseOver={handleMouseOver} onMouseOut={handleMouseOut}>
      <div className="cardFront">
        <h3 className="frontText">You're Invited!</h3>
        <div className="center-image" />
        <p className="names">Nazeem ‚ù§Ô∏è Faiza</p>
        <p className="date">12th November 2024</p>
      </div>
      <div className="cardInside">
        <h3 className="header">Nikah Invitation</h3>
        <p>Dear Guest,</p>
        <p>
          We warmly invite you to our Nikah ceremony to celebrate our love and union. Your presence means a lot.
        </p>
        <p className="location">üìç Royal Pearl Convention, Hyderabad</p>
        <p className="footer">Warm Regards,</p>
        <p className="family">Shaik Family</p>
      </div>
    </div>
  );
}
