import React, { useState, useEffect, useRef } from 'react';
import './BookInvite.css';
import { Howl } from 'howler';

import coverImage from './assets/cover.jpg';
import leftImage from './assets/left.jpg';
import rightImage from './assets/right.jpg';
import musicFile from './assets/nikah-music.mp3';

export default function BookInvite() {
  const [opened, setOpened] = useState(false);
  const [fullyOpened, setFullyOpened] = useState(false);
  const audioRef = useRef(null);

  useEffect(() => {
    audioRef.current = new Howl({
      src: [musicFile],
      html5: true,
      loop: true
    });
  }, []);

  useEffect(() => {
    if (opened) {
      audioRef.current.play();
      setTimeout(() => setFullyOpened(true), 600); // Delay for full open
    } else {
      setFullyOpened(false);
      audioRef.current.stop();
    }
  }, [opened]);

  const toggleBook = () => {
    setOpened(prev => !prev);
  };

  return (
    <div className={`book-wrapper ${opened ? 'shower' : ''}`} onClick={toggleBook}>
      {!opened && (
        <div className="book-cover shake">
          <div className="circle-image">
            <img src={coverImage} alt="Cover" />
          </div>
          <h2 className="cover-title">You're Invited</h2>
          <p className="cover-sub">To Our Nikah</p>
        </div>
      )}

      {opened && (
        <div className={`book-pages ${fullyOpened ? 'joined' : 'gap'}`}>
          <div className="page left">
            <div className="circle-image">
              <img src={leftImage} alt="Left" />
            </div>
            <p className="page-text">Nazeem ❤️</p>
          </div>
          <div className="page right">
            <div className="circle-image">
              <img src={rightImage} alt="Right" />
            </div>
            <p className="page-text">❤️ Faiza</p>
          </div>
        </div>
      )}

      {opened && <div className="flowers" />}
    </div>
  );
}
