import React, { useRef, useEffect, useState } from 'react';
import { Howl } from 'howler';
import './NikahBook.css';

import musicFile from '../src/assets/Nikah-Music.mp3';
import frontImage from '../src/assets/nikah-cover.jpg'; // cover image
import leftPageImage from '../src/assets/nikah-inside-left.jpg'; // left inside image

export default function NikahBook() {
  const [opened, setOpened] = useState(false);
  const audioRef = useRef(null);

  useEffect(() => {
    const sound = new Howl({ src: [musicFile], html5: true });
    audioRef.current = sound;
    return () => sound.unload();
  }, []);

  const handleClick = () => {
    setOpened(!opened);
    if (!opened) audioRef.current.play();
    else audioRef.current.stop();
  };

  return (
    <div className={`book-wrapper ${opened ? 'shower' : ''}`}>
      <div className="book">
        {/* Left Page - only visible after open */}
        {opened && (
          <div className="page left-page">
            <img src={leftPageImage} alt="Left Design" />
          </div>
        )}

        {/* Right Cover - flips open */}
        <div
          className={`page cover ${opened ? 'open' : ''}`}
          style={{ backgroundImage: `url(${frontImage})` }}
          onClick={handleClick}
        >
          {!opened && (
            <div className="cover-text">
              <h3>You're Invited</h3>
              <h2>Nazeem ‚ù§Ô∏è Faiza</h2>
              <p>12th November 2024</p>
            </div>
          )}
        </div>

        {/* Right Page (inside) */}
        {opened && (
          <div className="page right-page">
            <h3>Nikah Ceremony</h3>
            <p>We joyfully invite you to celebrate the wedding of</p>
            <h2>Nazeem & Faiza</h2>
            <p>üìç Royal Pearl Convention, Hyderabad</p>
            <p>üïí 6:00 PM Onwards</p>
          </div>
        )}
      </div>
      <div className="flowers" />
    </div>
  );
}
