import React, { useState, useRef, useEffect } from 'react';
import './NikahBook.css';
import { Howl } from 'howler';

import musicFile from './assets/nikah-music.mp3';
import coverImage from './assets/cover-image.jpg';
import leftImage from './assets/left-page.jpg';
import rightImage from './assets/right-page.jpg';

export default function BookInvite() {
  const [opened, setOpened] = useState(false);
  const [shaking, setShaking] = useState(false);
  const audioRef = useRef(null);

  useEffect(() => {
    const sound = new Howl({
      src: [musicFile],
      html5: true
    });
    audioRef.current = sound;
    return () => sound.unload();
  }, []);

  const handleClick = () => {
    if (!opened) {
      setShaking(true);
      setTimeout(() => {
        setShaking(false);
        setOpened(true);
        audioRef.current.play();
      }, 800);
    }
  };

  return (
    <div className={`book-stage ${opened ? 'shower' : ''}`}>
      <div className={`book ${shaking ? 'shake' : ''} ${opened ? 'open' : ''}`} onClick={handleClick}>
        <div className="book-cover">
          <img src={coverImage} alt="Cover" />
        </div>
        {opened && (
          <>
            <div className="book-page left">
              <img src={leftImage} alt="Left" />
            </div>
            <div className="book-page right">
              <img src={rightImage} alt="Right" />
            </div>
          </>
        )}
      </div>
      <div className="flowers" />
    </div>
  );
}
