import React, { useRef, useEffect } from 'react';
import { Howl } from 'howler';

import musicFile from '../src/assets/Nikah-Music.mp3'; // your nikah music file

export default function NikahCard() {
  const audioRef = useRef(null);

  useEffect(() => {
    const sound = new Howl({ src: [musicFile], html5: true });
    audioRef.current = sound;

    return () => sound.unload();
  }, []);

  const handleMouseOver = () => {
    audioRef.current.play();
  };

  const handleMouseOut = () => {
    audioRef.current.pause();
    audioRef.current.seek(0);
  };

  return (
    <div className="birthdayCard" onMouseOver={handleMouseOver} onMouseOut={handleMouseOut}>
      <div className="cardFront">
        <h3 className="happy">You're Invited</h3>
        <div className="center-logo" />
        <h3 className="names">Nazeem ‚ù§Ô∏è Faiza</h3>
        <p className="date">12th November 2024</p>
      </div>
      <div className="cardInside">
        <h3 className="back">Nikah Invitation</h3>
        <p>Dear Guest,</p>
        <p>
          Join us in celebrating our special day as we begin this beautiful journey together.
        </p>
        <p className="venue">üìç Royal Pearl Convention, Hyderabad</p>
        <p className="closing">Warm Regards,</p>
        <p className="family">Shaik Family</p>
      </div>
    </div>
  );
}
