import React, { useState, useRef, useEffect } from 'react';
import { Howl } from 'howler';
import './NikahBook.css';

import musicFile from '../src/assets/Nikah-Music.mp3';
import coverImage from '../src/assets/nikah-cover.jpg';
import leftImage from '../src/assets/nikah-inside-left.jpg';

export default function NikahBook() {
  const [open, setOpen] = useState(false);
  const audioRef = useRef(null);

  useEffect(() => {
    const sound = new Howl({ src: [musicFile], html5: true });
    audioRef.current = sound;
    return () => sound.unload();
  }, []);

  const handleClick = () => {
    setOpen(prev => {
      if (!prev) {
        audioRef.current.play();
      } else {
        audioRef.current.stop();
      }
      return !prev;
    });
  };

  return (
    <div className={`book-wrapper ${open ? 'shower' : ''}`}>
      <div className="book-container" onClick={handleClick}>
        {/* Left Page - shown after open */}
        <div className="page left-page">
          {open && <img src={leftImage} alt="Inside Left" />}
        </div>

        {/* Right Cover Page */}
        <div className={`page right-cover ${open ? 'open' : ''}`}>
          {!open && (
            <div className="cover-content">
              <h2>You're Invited!</h2>
              <h3>Nazeem ‚ù§Ô∏è Faiza</h3>
              <p>12th November 2024</p>
            </div>
          )}
        </div>

        {/* Right Page - shown after open */}
        {open && (
          <div className="page right-page">
            <h3>Nikah Ceremony</h3>
            <p>We joyfully invite you to celebrate the wedding of</p>
            <h2>Nazeem & Faiza</h2>
            <p>üìç Royal Pearl Convention, Hyderabad</p>
            <p>üïí 6:00 PM Onwards</p>
          </div>
        )}
      </div>
      <div className="flowers" />
    </div>
  );
}
